import React, { useContext, useState, useEffect } from "react";
import http from "../../utils/http-common";
import Account from "../Main/Accounts/Account";
import { CustomerContext } from "../../utils/CustomerContext";

const CustomerAccounts = () => {

    const { globalCustomer } = useContext(CustomerContext);
    const [customerAccounts, setCustomerAccounts] = useState([]);

    function getCustomerAccounts(globalCustomer) {
      http
        .get("/accounts")
        .then(function (response) {
          console.log(response.data.accounts);
          setCustomerAccounts(response.data.accounts)
        })
        .catch(function (err) {
          console.log(err);
        });
    }

    var custAcc = [];
    function setAccounts(globalCustomer) {
        customerAccounts.map((account, index)=>{
            if(globalCustomer.id === account.customer_id) {
                return (
                  <Account
                    key={index}
                    // click={() => props.clicked(index)}
                    customer_id={account.customer_id}
                    address={account.address}
                    city={account.city}
                    state={account.state}
                    zip_code={account.zip_code}
                    solar_farm_id={account.solar_farm_id}
                    capacity_share={account.capacity_share}
                    created_date={account.created_date}
                  />
                )
            }
        })
    }

    useEffect(() => {
      getCustomerAccounts(globalCustomer);
      setAccounts(globalCustomer);
    }, [globalCustomer]);

    return (
      <>
        <div>
          <h2>Customer's Accounts:</h2>
          <table>
            <thead>
              <tr>
                <th>Customer ID</th>
                <th>Address</th>
                <th>City</th>
                <th>State</th>
                <th>Zip Code</th>
                <th>Solar Farm ID</th>
                <th>Capacity Share</th>
                <th>Created On</th>
              </tr>
            </thead>
            <tbody>

              {/* {customerAccounts.map((account, index) => {
                return (
                  <Account
                    key={index}
                    // click={() => props.clicked(index)}
                    customer_id={account.customer_id}
                    address={account.address}
                    city={account.city}
                    state={account.state}
                    zip_code={account.zip_code}
                    solar_farm_id={account.solar_farm_id}
                    capacity_share={account.capacity_share}
                    created_date={account.created_date}
                  />
                );
              })} */}
            </tbody>
          </table>
        </div>
      </>
    );

}

export default CustomerAccounts;
